//Formato de la encuesta
function getFormEncuesta(crit, criterio, numPregunta, pregunta, sugerencias, nivel){
	var txt = "<form name='evalForma' id='evalForma' action='Evaluar/Evaluar.php' method='POST' target='mytarget' enctype='multipart/form-data' onSubmit='cargaPreguntaNuevamente(" + crit + ")'><table class='tabla' align='justify'><tr><th colspan='2'>ENCUESTA</th></tr>"
	txt += "<tr><td>Criterio</td><td><div align='left'>" + criterio + "</div></td></tr>";
	txt += "<tr><td>" + numPregunta + "</td><td><div align='left'>" + pregunta + "</div></td></tr>";
	txt += "<tr><td>Sugerencias</td><td><div align='left'>" + sugerencias + "</div></td></tr>";
	txt += "<tr><td>Evidencia</td><td><textarea id='tevi' name='tevi' cols='50' rows='4'></textarea></td></tr>";
	txt += "<tr><td></td><td>Elija un archivo a subir como evidencia:&nbsp;<input type='file' id='aevi' name='aevi'></td></tr>";
	for(var i = 0; i < nivel.length; i++){
		if(i == 0)
			txt += "<tr><td rowspan='" + (nivel.length + 1) + "'>Escala</td><td align='right'><div>" + nivel[i] + "</div></td></tr>";
		else
			txt += "<tr><td align='right'><div>" + nivel[i] + "</div></td></tr>";
	}
	txt += "<tr><td><div align='right'><table><tr><td><div align='left'>Calificaci&oacute;n</div></td><td><input id='calif' name='calif' type='text' size='3' maxlength='3'></td></tr></table></div></td></tr>";
	txt += "<tr><td colspan='2'><input type='hidden' id='oculto' name='oculto' value='eval'><input type='hidden' id='numPregunta' name='numPregunta' value='" + numPregunta + "'><input type='submit' name='enviaCalif' value='Enviar'></td></tr>";
	txt += "</table></form><iframe id='mytarget' name='mytarget' onLoad='cargado(this)'>s</iframe>";
	return txt;
}

